import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';

const HistorialCommits = () => {
  const token = localStorage.getItem('token');
  const userId = token ? jwtDecode(token).id : null;
  const [commits, setCommits] = useState([]); // Estado para los comentarios

  useEffect(() => {
    const fetchCommits = async () => {
      try {
        const projectId = new URLSearchParams(window.location.search).get('projectId'); // Obtener projectId de la URL
        const response = await axios.get(
          `http://localhost:5000/projects/${projectId}/commits`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setCommits(response.data);
      } catch (error) {
        console.error('Error al obtener los commits:', error.message);
      }
    };

    fetchCommits();
  }, [token]);

  return (
    <div className="main-layout">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>NetBurrito</h2>
        <ul>
          <li><Link to="/dashboard">Dashboard</Link></li>
          <li><Link to="/userprofile">Perfil de Usuario</Link></li>
          <li>
            <button
              onClick={() => {
                if (window.confirm('¿Estás seguro de que deseas eliminar tu cuenta?')) {
                  localStorage.removeItem('token');
                  alert('Cuenta eliminada.');
                  window.location.href = '/login';
                }
              }}
            >
              Eliminar Cuenta
            </button>
          </li>
        </ul>
      </div>

      {/* Contenido Principal */}
      <div className="main-content">
        <h1 className="main-title">Historial de Comentarios</h1>
        <div className="commit-list">
          {commits.length > 0 ? (
            commits.map((commit) => (
              <div key={commit._id} className="commit-card">
                <p className="commit-message">{commit.message}</p>
                <p className="commit-date">
                  <strong>Fecha:</strong> {new Date(commit.createdAt).toLocaleString()}
                </p>
              </div>
            ))
          ) : (
            <p>No hay comentarios para este proyecto.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default HistorialCommits;
