import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import {jwtDecode} from 'jwt-decode'; // Corrige la importación
import axios from 'axios';

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [profileImage, setProfileImage] = useState(null);
  const token = localStorage.getItem('token');

  // Decodificar el token para obtener el ID del usuario
  const userId = token ? jwtDecode(token).id : null;

  useEffect(() => {
    const fetchUserData = async () => {
      if (!userId) {
        console.error('No se encontró el ID del usuario');
        return;
      }

      try {
        const response = await axios.get(`http://localhost:5000/users/${userId}/profile`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, [userId, token]);

  const handleProfileImageUpload = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append('profileImage', file);

      try {
        const response = await axios.post('http://localhost:5000/users/upload-profile-image', formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data',
          },
        });

        setProfileImage(response.data.profileImageUrl);
        alert('Imagen de perfil actualizada');
      } catch (error) {
        console.error('Error uploading profile image:', error);
        alert('Error al subir la imagen de perfil');
      }
    }
  };

  return (
    <div className="main-layout">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>NetBurrito</h2>
        <ul>
          <li><Link to="/dashboard">Dashboard</Link></li>
          <li><Link to="/projectlist">Lista de Proyectos</Link></li>
          <li><Link to="/createproject">Crear Proyecto</Link></li>
        </ul>
      </div>

      {/* Contenido Principal */}
      <div className="main-content">
        {user ? (
          <div className="user-profile">
            <h1>Perfil de Usuario</h1>

            {/* Imagen de perfil */}
            <div className="profile-image">
              <img
                src={profileImage || user.profileImage || 'https://via.placeholder.com/150'}
                alt="Imagen de Perfil"
                className="user-image"
              />
              <input type="file" onChange={handleProfileImageUpload} accept="image/*" />
            </div>

            {/* Información del usuario */}
            <h2>{user.username}</h2>
            <p>{user.email}</p>

            {/* Seguidores y seguidos */}
            <div className="follow-info">
              <p>Seguidores: {user.followers.length}</p>
              <p>Seguidos: {user.following.length}</p>
            </div>
          </div>
        ) : (
          <p>Cargando datos del usuario...</p>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
