//project_frontend/src/components/Project/ProjectDetail.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const ProjectDetail = () => {
  const { id } = useParams();
  const [project, setProject] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProject = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const response = await axios.get(`http://localhost:5000/projects/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setProject(response.data);
      } catch (error) {
        console.error('Error al cargar detalles del proyecto:', error);
        alert('Error al cargar los detalles del proyecto.');
      } finally {
        setLoading(false);
      }
    };

    fetchProject();
  }, [id, navigate]);

  return (
    <div className="container mt-5">
      {loading ? (
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Cargando...</span>
          </div>
        </div>
      ) : project ? (
        <div>
          <h1>{project.name}</h1>
          <p>{project.description}</p>
          <button className="btn btn-secondary" onClick={() => navigate('/dashboard')}>
            Volver al Dashboard
          </button>
        </div>
      ) : (
        <p>El proyecto no existe o no est√° disponible.</p>
      )}
    </div>
  );
};

export default ProjectDetail;
