//Burrito_Net\project_frontend\src\components\User\EditProfile.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const EditProject = () => {
  const { id } = useParams();
  const [projectName, setProjectName] = useState('');
  const [projectDescription, setProjectDescription] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProject = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/projects/${id}`);
        setProjectName(response.data.name);
        setProjectDescription(response.data.description);
      } catch (error) {
        console.error('Error al cargar el proyecto:', error);
      }
    };

    fetchProject();
  }, [id]);

  const handleUpdateProject = async (e) => {
    e.preventDefault();
    try {
      await axios.put(
        `http://localhost:5000/projects/${id}`,
        { name: projectName, description: projectDescription },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      navigate('/dashboard');
    } catch (error) {
      console.error('Error al actualizar el proyecto:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h3>Editar Proyecto</h3>
      <form onSubmit={handleUpdateProject}>
        <div className="mb-3">
          <label htmlFor="projectName" className="form-label">Nombre del Proyecto</label>
          <input
            type="text"
            id="projectName"
            className="form-control"
            value={projectName}
            onChange={(e) => setProjectName(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="projectDescription" className="form-label">Descripci√≥n</label>
          <textarea
            id="projectDescription"
            className="form-control"
            rows="4"
            value={projectDescription}
            onChange={(e) => setProjectDescription(e.target.value)}
            required
          ></textarea>
        </div>
        <button type="submit" className="btn btn-success w-100">Guardar Cambios</button>
      </form>
    </div>
  );
};

export default EditProject;
