import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProjects = async () => {
      setLoading(true); // Establecer loading en true antes de la solicitud
      try {
        const response = await axios.get('http://localhost:5000/projects');
        setProjects(response.data);
      } catch (error) {
        console.error('Error fetching projects:', error);
      } finally {
        setLoading(false); // Establecer loading en false después de la solicitud
      }
    };

    fetchProjects();
  }, []);

  const handleLogout = () => {
    // Implementar la lógica para cerrar sesión (ej. limpiar el token del local storage)
    console.log('User logged out');
  };

  const handleUploadProject = () => {
    // Redirigir o mostrar un modal para subir proyecto
    alert('Subir proyecto funcionalidad aún no implementada');
  };

  return (
    <div className="dashboard">
      <div className="header">
        <h1>NetBurrito</h1>
        <img className="logo" src="/path/to/logo.png" alt="Logo" /> {/* Aquí iría tu logo */}
      </div>

      {/* Botones para navegar */}
      <div className="navigation-buttons">
        <Link to="/signup"><button className="auth-button">Sign Up</button></Link>
        <Link to="/login"><button className="auth-button">Login</button></Link>
        <button className="auth-button" onClick={handleLogout}>Logout</button>
        <button className="upload-project-btn" onClick={handleUploadProject}>Subir Proyecto</button>
      </div>

      {/* Mostrar proyectos más recientes */}
      <h2>Proyectos Recientes</h2>
      {loading ? (
        <p>Cargando proyectos...</p>
      ) : (
        <ul>
          {projects.map((project) => (
            <li key={project._id}>
              <h3>{project.name}</h3>
              <p>{project.description}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Dashboard;
