import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';  // Para redireccionar al dashboard después de crear el proyecto

const CreateProject = () => {
  const [projectName, setProjectName] = useState('');
  const [projectDescription, setProjectDescription] = useState('');
  const [tags, setTags] = useState([]);
  const [selectedTags, setSelectedTags] = useState([]);
  const navigate = useNavigate();  // Usamos useNavigate para redireccionar después de crear el proyecto

  useEffect(() => {
    // Obtener los tags predeterminados desde el backend
    const fetchTags = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tags');  // Ajusta la ruta si es necesario
        setTags(response.data);  // Suponemos que el backend devuelve una lista de tags
      } catch (error) {
        console.error('Error fetching tags:', error);
      }
    };

    fetchTags();
  }, []);

  const handleTagChange = (e) => {
    const value = Array.from(e.target.selectedOptions, option => option.value);
    setSelectedTags(value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newProject = {
      name: projectName,
      description: projectDescription,
      tags: selectedTags,
    };

    try {
      const response = await axios.post('http://localhost:5000/projects', newProject, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,  // Asegúrate de incluir el token de autenticación
        },
      });
      console.log('Proyecto creado:', response.data);
      // Redirigir al dashboard después de crear el proyecto
      navigate('/dashboard');
    } catch (error) {
      console.error('Error creando el proyecto:', error);
    }
  };

  return (
    <div className="create-project">
      <h1>Crear Proyecto</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="projectName">Nombre del Proyecto</label>
          <input
            type="text"
            id="projectName"
            value={projectName}
            onChange={(e) => setProjectName(e.target.value)}
            placeholder="Ingresa el nombre del proyecto"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="projectDescription">Descripción</label>
          <textarea
            id="projectDescription"
            value={projectDescription}
            onChange={(e) => setProjectDescription(e.target.value)}
            placeholder="Ingresa la descripción"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="tags">Selecciona los tags</label>
          <select
            id="tags"
            multiple
            value={selectedTags}
            onChange={handleTagChange}
            required
          >
            {tags.map((tag, index) => (
              <option key={index} value={tag}>
                {tag}
              </option>
            ))}
          </select>
        </div>

        <button type="submit">Crear Proyecto</button>
      </form>
    </div>
  );
};

export default CreateProject;
