import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [projects, setProjects] = useState([]); // Proyectos cargados desde el backend
  const [searchQuery, setSearchQuery] = useState(''); // Consulta de búsqueda
  const [loading, setLoading] = useState(true); // Estado de carga
  const navigate = useNavigate();

  // Obtener todos los proyectos desde el backend
  const fetchProjects = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/projects', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setProjects(response.data); // Almacena los proyectos en el estado
    } catch (error) {
      console.error('Error al obtener proyectos:', error.response?.data || error.message);
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetchProjects(); // Cargar proyectos al montar el componente
    } else {
      navigate('/login');
    }
  }, [navigate]);

  // Filtrar proyectos basados en el término de búsqueda
  const filteredProjects = projects.filter((project) =>
    project.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    project.tags.some((tag) => tag.toLowerCase().includes(searchQuery.toLowerCase()))
  );

  // Manejar el cambio en la búsqueda
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value); // Actualiza la consulta de búsqueda
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login'); // Redirigir al login
  };

  return (
    <div className="dashboard main-layout">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>NetBurrito</h2>
        <ul>
          <li>
            <Link to="/createproject">Subir Proyecto</Link>
          </li>
          <li>
            <Link to="/projectlist">Lista de Proyectos</Link>
          </li>
          <li>
            <Link to="/userprofile">Perfil de Usuario</Link>
          </li>
          <li>
            <button onClick={handleLogout}>Cerrar Sesión</button>
          </li>
        </ul>
      </div>

      {/* Contenido Principal */}
      <div className="main-content">
        <h2 className="main-title">Proyectos Recientes</h2>

        {/* Barra de búsqueda */}
        <div className="search-bar">
          <input
            type="text"
            placeholder="Buscar proyectos por nombre o tags..."
            value={searchQuery}
            onChange={handleSearchChange}
            className="search-input"
          />
        </div>

        {loading ? (
          <p className="loading-text">Cargando proyectos...</p>
        ) : filteredProjects.length > 0 ? (
          <ul className="project-list">
            {filteredProjects.map((project) => (
              <li key={project._id} className="project-item">
                <h3 className="project-title">{project.name}</h3>
                <p className="project-description">{project.description.substring(0, 100)}...</p>
                <p className="project-creator">Creado por: {project.createdByName || 'Usuario Desconocido'}</p>
                {project.fileUrl && (
                  <div className="project-file">
                    {project.fileType?.startsWith('image/') ? (
                      <img
                        src={project.fileUrl}
                        alt={project.name}
                        className="project-image"
                      />
                    ) : (
                      <div className="button-container">
                        <a href={project.fileUrl} download={project.fileName}>
                          <button className="project-btn">Descargar Archivo ({project.fileName})</button>
                        </a>
                      </div>
                    )}
                  </div>
                )}
                <button
                  className="project-btn view-details"
                  onClick={() => navigate(`/projectdetails/${project._id}`)}
                >
                  Ver Detalles
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p className="no-results-text">No se encontraron proyectos.</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
