//Burrito_Net\project_frontend\src\components\Project\CreateProject.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const CreateProject = () => {
  const [projectName, setProjectName] = useState('');
  const [projectDescription, setProjectDescription] = useState('');
  const [tags, setTags] = useState('');
  const [selectedTags, setSelectedTags] = useState([]);
  const [loading, setLoading] = useState(false); // Indicador de carga
  const navigate = useNavigate();

  const handleTagChange = (e) => {
    setTags(e.target.value);
  };

  const handleAddTag = () => {
    if (tags.trim() && !selectedTags.includes(tags.trim())) {
      if (selectedTags.length >= 10) {
        alert('No puedes agregar más de 10 etiquetas.');
        return;
      }
      setSelectedTags([...selectedTags, tags.trim()]);
      setTags('');
    }
  };

  const handleTagDelete = (tagToDelete) => {
    setSelectedTags(selectedTags.filter((tag) => tag !== tagToDelete));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Inicia el indicador de carga

    const newProject = {
      name: projectName,
      description: projectDescription,
      tags: selectedTags,
    };

    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/projects/`, newProject, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      console.log('Proyecto creado:', response.data);
      navigate('/dashboard');
    } catch (error) {
      console.error('Error creando el proyecto:', error.response?.data || error.message);
      alert('Error creando el proyecto. Por favor, inténtalo de nuevo.');
    } finally {
      setLoading(false); // Detiene el indicador de carga
    }
  };

  return (
    <div className="create-project">
      <h1>Crear Proyecto</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="projectName">Nombre del Proyecto</label>
          <input
            type="text"
            id="projectName"
            value={projectName}
            onChange={(e) => setProjectName(e.target.value)}
            placeholder="Ingresa el nombre del proyecto"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="projectDescription">Descripción</label>
          <textarea
            id="projectDescription"
            value={projectDescription}
            onChange={(e) => setProjectDescription(e.target.value)}
            placeholder="Ingresa la descripción"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="tags">Agregar Tags</label>
          <div>
            <input
              type="text"
              id="tags"
              value={tags}
              onChange={handleTagChange}
              placeholder="Escribe un tag y presiona Enter"
            />
            <button type="button" onClick={handleAddTag} disabled={selectedTags.length >= 10}>
              Agregar Tag
            </button>
          </div>
          <div className="tags-container">
            {selectedTags.map((tag, index) => (
              <span key={index} className="tag">
                {tag}
                <button type="button" onClick={() => handleTagDelete(tag)}>X</button>
              </span>
            ))}
          </div>
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Creando...' : 'Crear Proyecto'}
        </button>
      </form>
    </div>
  );
};

export default CreateProject;
