// src/components/Auth/Login.js
import React, { useState } from 'react'; 
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Importamos useNavigate

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); // Hook para redirigir

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/auth/login', {
        email,
        password,
      });

      const token = response.data.token;
      // Guardamos el token en el localStorage
      localStorage.setItem('token', token);
      console.log('Login exitoso, token:', token);

      // Redirigir al Dashboard después de un login exitoso
      navigate('/dashboard');
    } catch (error) {
      console.error('Login error:', error.response?.data || error.message);
      alert('Error al iniciar sesión, por favor revisa tus credenciales');
    }
  };

  // Función para manejar la redirección al SignUp
  const handleGoToSignUp = () => {
    navigate('/signup'); // Redirige al componente SignUp
  };

  return (
    <section className="py-5 py-lg-8 py-xl-10">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-lg-6 col-md-8">
            <div className="card shadow-sm">
              <div className="row g-0">
                <div className="col-lg-6 d-flex justify-content-center align-items-center">
                  <img
                    src="https://content.cuerpomente.com/medio/2022/09/08/dibujo-nino_6db16aa5_1200x1200.jpg"
                    alt="Dibujo"
                    className="img-fluid rounded-start"
                    style={{ minHeight: '400px', objectFit: 'cover' }}
                  />
                </div>
                <div className="col-lg-6 p-4">
                  <h5 className="mb-4">Log in to your account</h5>
                  <form onSubmit={handleLogin}>
                    <div className="mb-3">
                      <label className="form-label" htmlFor="email">Email</label>
                      <input
                        className="form-control"
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        id="email"
                        placeholder="pat@saturn.dev"
                        required
                      />
                    </div>
                    <div className="mb-3">
                      <label className="form-label" htmlFor="password">Password</label>
                      <input
                        className="form-control"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        id="password"
                        placeholder="Enter your password"
                        required
                      />
                    </div>
                    <button className="btn btn-primary w-100" type="submit">Login</button>
                  </form>
                  <p className="mt-3 text-center">
                    <span>Don’t have an account?</span>
                    <button 
                      className="btn btn-link p-0" 
                      type="button" 
                      onClick={handleGoToSignUp}
                    >
                      Sign up
                    </button>
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Login;
